CREATE TABLE Tickets(
   Sales_Id        INTEGER  NOT NULL PRIMARY KEY 
  ,Event           VARCHAR(20) NOT NULL
  ,Customer        VARCHAR(15) NOT NULL
  ,Adult_Tickets   INTEGER  NOT NULL
  ,Child_Tickets   INTEGER  NOT NULL
  ,Ttl_Ticketcost  INTEGER  NOT NULL
  ,Parking_Cost    INTEGER  NOT NULL
  ,Total_Sale      INTEGER  NOT NULL
  ,Reservation_num VARCHAR(14) NOT NULL
);
